mapper.delete:
delete from notice

    fromuid=#{fromuid}
    and touid=#{touid}
    and nid=#{nid}

mapper.select:
select * from notice



            fromuid=#{fromuid} and touid=#{touid} or fromuid=#{touid} and touid=#{fromuid}


            fromuid=#{fromuid}
            and touid=#{touid}
            and nid=#{nid}
            and isread=#{isread}



order by date
